var gulp=require("gulp"),htmlmin=require("gulp-htmlmin");gulp.task("html",function(){gulp.src("*.html").pipe(htmlmin({collapseWhitespace:!0,removeComments:!0})).pipe(gulp.dest("dist"))});var scss=require("gulp-sass"),cssnano=require("gulp-cssnano");gulp.task("scss",function(){gulp.src("*.scss").pipe(scss()).pipe(gulp.dest("dist")).pipe(cssnano()).pipe(gulp.dest("dist"))});var imagemin=require("gulp-imagemin"),cache=require("gulp-cache");gulp.task("image",function(){gulp.src("img/*.{jpg,png,gif}").pipe(cache(imagemin({progressive:!0,interlaced:!0}))).pipe(gulp.dest("dist/img"))});var uglify=require("gulp-uglify");gulp.task("js",function(){gulp.src("*.js").pipe(uglify()).pipe(gulp.dest("dist"))}),gulp.task("watch",["scss","js","image","html"],function(){gulp.watch("*.scss",["scss"]),gulp.watch("js/*.js",["js"]),gulp.watch("img/*.*",["image"]),gulp.watch("*.html",["html"])});